/*Table structure for table `book` */

DROP TABLE IF EXISTS `book`;

CREATE TABLE `book` (
  `book_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `book_name` varchar(256) NOT NULL COMMENT '图书名称',
  `book_sub_name` varchar(256) DEFAULT NULL COMMENT '产品副题',
  `book_full_name` varchar(256) DEFAULT NULL COMMENT '图书全称',
  `english_book_name` varchar(256) DEFAULT NULL COMMENT '英文名称',
  `book_uuid` char(36) NOT NULL DEFAULT 'Sequelize.UUIDV1' COMMENT '图书唯一ID',
  `book_state` tinyint(4) DEFAULT NULL COMMENT '图书状态',
  `info_src` tinyint(4) DEFAULT NULL COMMENT '图书来源',
  `unit` tinyint(4) DEFAULT NULL COMMENT '图书单位',
  `auto_code` varchar(128) DEFAULT NULL COMMENT '自编码',
  `bar_code` varchar(32) DEFAULT NULL COMMENT '条形码',
  `bar_code_image` varchar(128) DEFAULT NULL COMMENT '条形码图片',
  `isbn_code` varchar(32) DEFAULT NULL COMMENT 'ISBN',
  `book_price` decimal(19,2) DEFAULT NULL COMMENT '图书定价',
  `average_unitprice` decimal(19,6) DEFAULT NULL COMMENT '成本单价',
  `fore_discount` decimal(19,6) DEFAULT NULL COMMENT '预设折扣',
  `sale_price` decimal(19,2) DEFAULT NULL COMMENT '销售价',
  `pubpartner_id` int(11) DEFAULT NULL COMMENT '出版单位',
  `pubpartner_name` varchar(128) DEFAULT NULL COMMENT '出版单位名称',
  `supplier_id` int(11) DEFAULT NULL COMMENT '供货商ID',
  `supplier_commodity_id` varchar(64) DEFAULT NULL COMMENT '供货商商品ID',
  `dimensionformat_id` tinyint(4) DEFAULT NULL COMMENT '开数',
  `bookformat_id` tinyint(4) DEFAULT NULL COMMENT '开本',
  `materialpaper_id` tinyint(4) DEFAULT NULL COMMENT '材料用纸',
  `author_name` varchar(512) DEFAULT NULL COMMENT '原著编者',
  `translator_name` varchar(512) DEFAULT NULL COMMENT '译者姓名',
  `bindingformat_id` tinyint(4) DEFAULT NULL COMMENT '装帧形式',
  `booktype_id` tinyint(4) DEFAULT NULL COMMENT '图书类别',
  `responsibleeditor` varchar(128) DEFAULT NULL COMMENT '责任编辑',
  `publish_date` date DEFAULT NULL COMMENT '出版日期',
  `series_code` varchar(32) DEFAULT NULL COMMENT '丛书编号',
  `series_name` varchar(64) DEFAULT NULL COMMENT '丛书名称',
  `volume_number` smallint(6) DEFAULT NULL COMMENT '卷册数',
  `release_version` tinyint(4) DEFAULT NULL COMMENT '版次',
  `printing_version` tinyint(4) DEFAULT NULL COMMENT '印次',
  `booksnumber` smallint(6) DEFAULT NULL COMMENT '每包册书',
  `unitpiece` decimal(19,1) DEFAULT NULL COMMENT '每件包数',
  `avebookweight` decimal(19,1) DEFAULT NULL COMMENT '每册克重',
  `clawtype_id` smallint(6) DEFAULT NULL COMMENT '中图分类',
  `page_number` int(11) DEFAULT NULL COMMENT '页数',
  `word_number` decimal(13,2) DEFAULT NULL COMMENT '字数',
  `sheet_count` decimal(19,4) DEFAULT NULL COMMENT '印张数量',
  `ciptype_id` smallint(6) DEFAULT NULL COMMENT 'CIP分类',
  `ciptype_name` varchar(128) DEFAULT NULL COMMENT 'CIP分类名称',
  `subject_keywords` varchar(128) DEFAULT NULL COMMENT '主题词',
  `common_keywords` varchar(128) DEFAULT NULL COMMENT '普通关键词',
  `knowledge_keywords` varchar(128) DEFAULT NULL COMMENT '学科关键词',
  `event_keywords` varchar(128) DEFAULT NULL COMMENT '事件关键词',
  `printing_time` date DEFAULT NULL COMMENT '印刷时间',
  `composingtype_id` tinyint(4) DEFAULT NULL COMMENT '著作方式',
  `packing_id` tinyint(4) DEFAULT NULL COMMENT '包装',
  `book_cover` varchar(128) DEFAULT NULL COMMENT '图书正封面',
  `book_cover1` varchar(128) DEFAULT NULL COMMENT '图书封面1',
  `book_cover2` varchar(128) DEFAULT NULL COMMENT '图书封面2',
  `book_cover3` varchar(128) DEFAULT NULL COMMENT '图书封面3',
  `book_cover4` varchar(128) DEFAULT NULL COMMENT '图书封面4',
  `book_cover5` varchar(128) DEFAULT NULL COMMENT '图书封面5',
  `catalogue` mediumtext COMMENT '目录',
  `foreword` mediumtext COMMENT '前言概述',
  `editorial_review` mediumtext COMMENT '编辑推荐',
  `book_selling_point` mediumtext COMMENT '本书卖点',
  `reader` mediumtext COMMENT '目标读者',
  `media_review` mediumtext COMMENT '媒体评论',
  `experts_comment` mediumtext COMMENT '专家评论',
  `author_introduce` mediumtext COMMENT '作者简介',
  `content_abstract` mediumtext COMMENT '内容简介',
  `sort_index` int(11) DEFAULT NULL COMMENT '排序位置',
  `assign_class` smallint(6) DEFAULT NULL COMMENT '归属分类',
  `phonetic_key` varchar(512) DEFAULT NULL COMMENT '拼音键',
  `last_update` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  `sys_goods_class_id` int(11) DEFAULT NULL COMMENT '主商品类别',
  `classtags` varchar(1024) DEFAULT NULL COMMENT '子商品类别标签',
  PRIMARY KEY (`book_id`)
) ENGINE=InnoDB AUTO_INCREMENT=196 DEFAULT CHARSET=utf8;

