/*Table structure for table `commodity` */

DROP TABLE IF EXISTS `commodity`;

CREATE TABLE `commodity` (
  `commodity_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `commodity_name` varchar(256) NOT NULL COMMENT '商品名称',
  `commodity_aliasname` varchar(256) DEFAULT NULL COMMENT '商品简称',
  `commodity_uuid` char(36) NOT NULL DEFAULT 'Sequelize.UUIDV1' COMMENT '商品唯一ID',
  `commodity_code` varchar(128) DEFAULT NULL COMMENT '商品编码',
  `bar_code` varchar(32) DEFAULT NULL COMMENT '条形码',
  `bar_code_image` varchar(128) DEFAULT NULL COMMENT '条形码图片',
  `introduction` mediumtext COMMENT '商品介绍',
  `supplier_id` int(11) DEFAULT NULL COMMENT '供货商ID',
  `supplier_commodity_id` varchar(64) DEFAULT NULL COMMENT '供货商ID',
  `commodity_image` varchar(128) DEFAULT NULL COMMENT '商品图片',
  `commodity_price` decimal(19,6) NOT NULL COMMENT '商品定价',
  `commodity_discount` decimal(19,6) DEFAULT NULL COMMENT '商品折扣',
  `shelves_date` datetime DEFAULT NULL COMMENT '上架日期',
  `views_times` int(11) DEFAULT NULL COMMENT '浏览次数',
  `commodity_type` int(11) DEFAULT NULL COMMENT '商品分类',
  `share_times` int(11) DEFAULT NULL COMMENT '分享次数',
  `activity_times` int(11) DEFAULT NULL COMMENT '共参与活动数',
  `specifications` varchar(512) DEFAULT NULL COMMENT '商品规格',
  `commodity_property` varchar(512) DEFAULT NULL COMMENT '商品属性',
