/*Table structure for table `prize_investigation` */

DROP TABLE IF EXISTS `prize_investigation`;

CREATE TABLE `prize_investigation` (
  `prize_investigation_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `prize_investigation_title` varchar(256) NOT NULL COMMENT '调查标题',
  `prize_investigation_state` smallint(6) DEFAULT '1' COMMENT '调查状态',
  `prize_investigation_type` smallint(6) DEFAULT '1' COMMENT '调查类型',
  `prize_investigation_keywords1` varchar(128) DEFAULT NULL COMMENT '关键词1',
  `prize_investigation_keywords2` varchar(128) DEFAULT NULL COMMENT '关键词2',
  `prize_investigation_abstract` varchar(2048) DEFAULT NULL COMMENT '调查摘要',
  `prize_investigation_content` text COMMENT '调查内容',
  `click_times` int(11) unsigned DEFAULT NULL COMMENT '点击次数',
  `submitter` int(11) unsigned DEFAULT NULL COMMENT '提交人',
  `submit_date` datetime DEFAULT NULL COMMENT '提交时间',
  `auditor` int(11) unsigned DEFAULT NULL COMMENT '审核人',
  `audit_date` datetime DEFAULT NULL COMMENT '审核时间',
  `releaser` int(11) unsigned DEFAULT NULL COMMENT '发布人',
  `release_date` datetime DEFAULT NULL COMMENT '发布时间',
