  `prize_investigation_uuid` char(36) DEFAULT NULL COMMENT '调查唯一ID',
  `respondent` varchar(128) DEFAULT NULL COMMENT '应答者',
  `contact_way` varchar(128) DEFAULT NULL COMMENT '联系方式',
  PRIMARY KEY (`prize_investigation_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

