/*Table structure for table `prizerule` */

DROP TABLE IF EXISTS `prizerule`;

CREATE TABLE `prizerule` (
  `prizerule_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `prizerule_name` varchar(256) NOT NULL COMMENT '规则名称',
  `prizerule_formula` varchar(512) DEFAULT NULL COMMENT '规则计算公式',
  `izerule_content` varchar(512) DEFAULT NULL COMMENT '规则内容描述',
  `is_use` tinyint(4) DEFAULT NULL COMMENT '是否启用',
  PRIMARY KEY (`prizerule_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

