/*Table structure for table `sponsor` */

DROP TABLE IF EXISTS `sponsor`;

CREATE TABLE `sponsor` (
  `sponsor_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `sponsor_name` varchar(256) NOT NULL COMMENT '主办方名称',
  `sponsor_code` varchar(64) DEFAULT NULL COMMENT '主办方编码',
  `sponsor_full_name` varchar(512) DEFAULT NULL COMMENT '主办方全称',
  `sponsor_english_name` varchar(512) DEFAULT NULL COMMENT '主办方外文名称',
  `sponsor_state` tinyint(4) DEFAULT NULL COMMENT '主办方状态',
  `current_state` tinyint(4) DEFAULT NULL COMMENT '往来状态',
  `sponsor_type` smallint(6) DEFAULT NULL COMMENT '主办方类型',
  `sponsor_property` smallint(6) DEFAULT NULL COMMENT '主办方属性',
  `office_address` varchar(512) DEFAULT NULL COMMENT '办公地址',
  `office_phone` varchar(32) DEFAULT NULL COMMENT '办公电话',
  `contact_address` varchar(512) DEFAULT NULL COMMENT '通讯地址',
  `postcode` varchar(6) DEFAULT NULL COMMENT '邮政编码',
  `juridical_person` varchar(32) DEFAULT NULL COMMENT '法定代表人',
  `web_address` varchar(32) DEFAULT NULL COMMENT '网站地址',
  `sponsor_email` varchar(32) DEFAULT NULL COMMENT '邮箱地址',
  `fax_phone` varchar(32) DEFAULT NULL COMMENT '传真电话',
  `mobile_phone` varchar(32) DEFAULT NULL COMMENT '移动电话',
  `contact_person` varchar(64) DEFAULT NULL COMMENT '联系人',
  `contact_phone` varchar(64) DEFAULT NULL COMMENT '联系人电话',
  `area_id` smallint(6) DEFAULT NULL COMMENT '区域ID',
  `province_id` smallint(6) DEFAULT NULL COMMENT '省别ID',
  `city_id` smallint(6) DEFAULT NULL COMMENT '市别ID',
  `popedom_id` smallint(6) DEFAULT NULL COMMENT '辖区ID',
  `account_name` varchar(128) DEFAULT NULL COMMENT '开户名称',
  `account_bank` varchar(128) DEFAULT NULL COMMENT '开户银行',
  `bank_account_code` varchar(128) DEFAULT NULL COMMENT '银行账号',
  `valueadded_tax_account` varchar(128) DEFAULT NULL COMMENT '增值税号',
  `register_no` varchar(128) DEFAULT NULL COMMENT '营业执照注册号',
  `sponsor_capital` varchar(64) DEFAULT NULL COMMENT '注册资本',
  `sponsor_taxrate` smallint(6) DEFAULT NULL COMMENT '企业税率',
  `sponsor_ascription` smallint(6) DEFAULT NULL COMMENT '主办方归属',
  `invoice_type` smallint(6) DEFAULT NULL COMMENT '发票种类',
  `invoice_rise` varchar(256) DEFAULT NULL COMMENT '发票抬头',
  `invoice_address` varchar(512) DEFAULT NULL COMMENT '开票地址',
  `invoice_item` varchar(256) DEFAULT NULL COMMENT '开票项目',
  `financial_code` varchar(64) DEFAULT NULL COMMENT '财务编码',
  `sponsor_uuid` char(36) DEFAULT NULL COMMENT '主办方唯一ID',
  `sort_index` int(11) DEFAULT NULL COMMENT '排序编号',
  `is_use` tinyint(4) DEFAULT '1' COMMENT '是否启用',
  `sponsor_introduce` text COMMENT '主办方简介',
  `sponsor_remarks` text COMMENT '内容简介',
  `sponsor_image` varchar(128) DEFAULT NULL COMMENT '主办方封面',
  `last_update` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  `is_cooperativepress` tinyint(4) DEFAULT '0' COMMENT '是否合作出版社',
  `phonetic_key` varchar(512) DEFAULT NULL COMMENT '拼音键',
  `is_publisher` tinyint(4) DEFAULT '0' COMMENT '是否显示在出版专区',
  `publishingzone_logo` varchar(128) DEFAULT NULL COMMENT '出版专区logo',
  `publishingzone_image` varchar(128) DEFAULT NULL COMMENT '专区宣传图',
  `sponsor_message` varchar(512) DEFAULT NULL COMMENT '社长寄语',
  PRIMARY KEY (`sponsor_id`)
) ENGINE=InnoDB AUTO_INCREMENT=561 DEFAULT CHARSET=utf8;

