丛书》等。</p>',NULL,'1484190409457.jpg','2017-01-12 15:59:48',0,'hubeirenminchubanshe',1,'1484190385482.jpg','',''),(156,'湖北少年儿童出版社','5353',NULL,NULL,1,NULL,NULL,NULL,'武汉',NULL,NULL,'430077',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(157,'湖南出版社','5438',NULL,NULL,1,NULL,NULL,NULL,'长沙',NULL,NULL,'430077',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(158,'湖南大学出版社','314',NULL,NULL,1,NULL,NULL,NULL,'长沙',NULL,NULL,'420012',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(159,'湖南地图出版社','80552',NULL,NULL,1,NULL,NULL,NULL,'长沙',NULL,NULL,'410004',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(160,'湖南教育出版社','5355',NULL,NULL,1,NULL,NULL,NULL,'长沙',NULL,NULL,'410005',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(161,'湖南科学技术出版社','5357',NULL,NULL,1,NULL,NULL,NULL,'长沙',NULL,NULL,'410005',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(162,'湖南美术出版社','5356',NULL,NULL,1,NULL,NULL,NULL,'长沙',NULL,NULL,'410011',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:10',0,NULL,0,NULL,NULL,NULL),(163,'湖南人民出版社','2