，使其教育图书同样具备领先优势。</strong></span></p>'),(303,'上海财经大学出版社','81049',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200040',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(304,'上海辞书出版社','5326',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200040',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(305,'上海古籍出版社','5325',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200031',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(306,'上海画报出版社','80530',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200233',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(307,'上海交通大学出版社','313',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200052',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(308,'上海教育出版社','5320',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200052',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(309,'上海科技教育出版社','5428',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200233',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,'2016-12-17 14:07:11',0,NULL,0,NULL,NULL,NULL),(310,'上海科学技术出版社','5323',NULL,NULL,1,NULL,NULL,NULL,'上海',NULL,NULL,'200020',NULL,NULL,